import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Form, Row, Col } from "react-bootstrap";
import { Formik } from "formik";
import * as yup from "yup";

//定義錯誤訊息及驗證
const schema = yup.object().shape({
  store: yup.string().required("請選擇店家"),
  activity_name: yup.string().required("請輸入活動名稱"),
  startDate: yup.string().required("請選擇開始日期"),
  endDate: yup.string().required("請選擇結束日期"),
  promotion_code: yup.string().required("請輸入優惠代碼"),
  frequency: yup.string().required("請輸入總使用次數"),
  user_frequency: yup.string().required("請輸入使用者每次使用次數"),
  consumption: yup.number().required("請填入消費需達多少金額"),
  discount: yup.number().required("請填入總金額折價多少錢"),
});

function Coupon({
  startADay,
  setStartADay,
  toDayDate,
  endADay,
  setEndADay,
  maxDay,
}) {
  const history = useHistory();
  const [frequencyCheck, setFrequencyCheck] = useState(false);
  const [userFrequencyCheck, setUserFrequencyCheck] = useState(false);

  const [allStageChecked, setAllStageChecked] = useState(false);

  return (
    <Formik
      validationSchema={schema}
      onSubmit={(e) => {
        // setData([...data, e]);
        console.log(e);
      }}
      initialValues={{
        store: "",
        activity_name: "",
        startDate: "",
        endDate: "",
        promotion_code: "",
        frequency: "",
        user_frequency: "",
        consumption: "",
        discount: "",
        discountRadio: "fixed_discount",
      }}
    >
      {({ handleSubmit, handleChange, values, errors }) => (
        <Form noValidate onSubmit={handleSubmit}>
          <Form.Group className="my-3 " as={Row} controlId="validationFormik03">
            <Form.Label column sm={2}>
              適用店家
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                as="select"
                placeholder="請選擇"
                name="store"
                value={allStageChecked ? (values.store = "All") : values.store}
                onChange={handleChange}
                isInvalid={!!errors.store}
                disabled={allStageChecked ? "disabled" : ""}
              >
                <option value="">請選擇</option>
                <option value="1">店家1</option>
                <option value="2">店家2</option>
                <option value="3">店家3</option>
              </Form.Control>
              <Form.Check
                type="checkbox"
                label="適用全平台"
                checked={allStageChecked}
                onChange={() => {
                  setAllStageChecked(!allStageChecked);
                }}
              />
              <Form.Control.Feedback type="invalid">
                {errors.store}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>

          <Form.Group className="my-3" as={Row} controlId="validationFormik04">
            <Form.Label column sm={2}>
              活動名稱
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                type="text"
                placeholder="活動名稱"
                name="activity_name"
                value={values.activity_name}
                onChange={handleChange}
                isInvalid={!!errors.activity_name}
                maxLength="10"
              />
              <small style={{ color: "#768192" }}>EX:399折扣10元</small>
              <Form.Control.Feedback type="invalid">
                {errors.activity_name}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik05">
            <Form.Label column sm={2}>
              活動詳情
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                type="text"
                as="textarea"
                placeholder="請輸入內容"
                name="event_details"
                value={values.event_details}
                onChange={handleChange}
                isInvalid={!!errors.event_details}
              />
              <small style={{ color: "#768192" }}></small>
              <Form.Control.Feedback type="invalid">
                {errors.event_details}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik06">
            <Form.Label column sm={2}>
              日期起訖
            </Form.Label>
            <div className="col-sm-10 col-md-10 d-flex flex-wrap">
              <Col md={3} className="col-12">
                <Form.Control
                  type="date"
                  placeholder="活動名稱"
                  name="startDate"
                  value={startADay}
                  onChange={(e) => {
                    handleChange(e);
                    setStartADay(e.target.value);
                  }}
                  isInvalid={!!errors.startDate}
                  min={toDayDate}
                />
                <small style={{ color: "#768192" }}>
                  行銷活動最長只能持續七天
                </small>
                <Form.Control.Feedback type="invalid">
                  {errors.startDate}
                </Form.Control.Feedback>
              </Col>
              <Form.Label
                column
                md={1}
                className="d-flex justify-content-center "
              >
                ~
              </Form.Label>

              <Col md={3} className="col-12">
                <Form.Control
                  type="date"
                  placeholder="活動名稱"
                  name="endDate"
                  value={endADay}
                  onChange={(e) => {
                    handleChange(e);
                    setEndADay(e.target.value);
                  }}
                  isInvalid={!!errors.endDate}
                  min={endADay}
                  max={maxDay}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.endDate}
                </Form.Control.Feedback>
              </Col>
            </div>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik07">
            <Form.Label column sm={2}>
              優惠代碼
            </Form.Label>
            <Col sm={10} md={6}>
              <Form.Control
                type="text"
                name="promotion_code"
                value={values.promotion_code}
                onChange={handleChange}
                isInvalid={!!errors.promotion_code}
              />
              <Form.Control.Feedback type="invalid">
                {errors.promotion_code}
              </Form.Control.Feedback>
            </Col>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik08">
            <Form.Label column sm={2}>
              總使用次數
            </Form.Label>
            <Col sm={5} md={3}>
              <Form.Control
                type="number"
                // as="number"
                name="frequency"
                value={
                  frequencyCheck
                    ? (values.frequency = "無上限")
                    : values.frequency
                }
                onChange={handleChange}
                isInvalid={!!errors.frequency}
                disabled={frequencyCheck ? "disabled" : ""}
              />
              <Form.Control.Feedback type="invalid">
                {errors.frequency}
              </Form.Control.Feedback>
            </Col>
            <div className="col-md-3 col-sm-5 my-auto">
              <Form.Check
                type="checkbox"
                label="無上限"
                checked={frequencyCheck}
                onChange={() => {
                  setFrequencyCheck(!frequencyCheck);
                }}
              />
            </div>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik09">
            <Form.Label column sm={2}>
              每人每次使用次數
            </Form.Label>
            <Col sm={5} md={3}>
              <Form.Control
                type="number"
                // as="number"
                name="user_frequency"
                value={
                  userFrequencyCheck
                    ? (values.user_frequency = "無上限")
                    : values.user_frequency
                }
                onChange={handleChange}
                isInvalid={!!errors.user_frequency}
                disabled={userFrequencyCheck ? "dosabled" : ""}
              />
              <Form.Control.Feedback type="invalid">
                {errors.user_frequency}
              </Form.Control.Feedback>
            </Col>
            <div className="col-md-3 col-sm-5 my-auto">
              <Form.Check
                type="checkbox"
                label="無上限"
                checked={userFrequencyCheck}
                onChange={() => {
                  setUserFrequencyCheck(!userFrequencyCheck);
                }}
              />
            </div>
          </Form.Group>
          <Form.Group className="my-3" as={Row} controlId="validationFormik11">
            <Form.Label column sm={2}>
              折扣方式
            </Form.Label>
            <Col sm={10} md={6}>
              <div className="d-flex">
                <div
                  className="d-flex pt-1 "
                  onChange={handleChange}
                  value={values.discountRadio}
                >
                  <Form.Check
                    className="me-3"
                    name="discountRadio"
                    type="radio"
                    value="fixed_discount"
                    label="固定折價"
                    id="fixed_discount"
                    defaultChecked={values.discountRadio === "fixed_discount"}
                  />
                  <Form.Check
                    className="me-3"
                    name="discountRadio"
                    type="radio"
                    value="percentage"
                    label="折扣百分比"
                    id="percentage"
                    defaultChecked={values.discountRadio === "percentage"}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.percentage}
                  </Form.Control.Feedback>
                </div>
              </div>
              <div className="d-flex align-items-center">
                <p style={{ flexShrink: "0" }}>消費達</p>
                <Col className="px-2">
                  <Form.Control
                    className="mx-2"
                    type="number"
                    name="consumption"
                    value={values.consumption}
                    onChange={handleChange}
                    isInvalid={!!errors.consumption}
                    style={{ height: "40px" }}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.consumption}
                  </Form.Control.Feedback>
                </Col>
                <p style={{ flexShrink: "0" }}>元,總金額折價</p>
                <Col className="px-2">
                  <Form.Control
                    className="mx-2"
                    type="number"
                    name="discount"
                    value={values.discount}
                    onChange={handleChange}
                    isInvalid={!!errors.discount}
                    style={{ height: "40px" }}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.discount}
                  </Form.Control.Feedback>
                </Col>
                <p>{values.discountRadio === "percentage" ? "折" : "元"}</p>
              </div>
              <small style={{ color: "#768192" }}>
                消費金額必須大於折價金額
              </small>
            </Col>
          </Form.Group>
          {/* 送出紐 */}
          <Form.Group
            as={Col}
            // md="1"
            controlId="validationFormikUsername"
            className="d-flex justify-content-center my-3"
          >
            <button
              type="submit"
              className="btn btn-light  mx-2"
              onClick={() => {
                history.push("marketing");
              }}
            >
              返回
            </button>
            <button type="submit" className="btn btn-success text-white mx-2">
              確認
            </button>
          </Form.Group>
        </Form>
      )}
    </Formik>
  );
}

export default Coupon;
