import * as Reconciliationdata from "../../../components/Json/SalesReportData.json"
import * as ReconciliationProductdata from "../../../components/Json/SalesReportProduct.json"
import * as ReconciliationLabel from "../../../components/Json/ReconciliationLabel.json"
import ReactDOM from 'react-dom';
// 引入按鈕
import { ClickMe, DetailedBtn, ReconciliationBtns, ChildReconciliationBtns } from "../../../components/ReactElement/ColorBtn";
// 引入函式
import { UnfoldTd, UnfoldTdTop, UnfoldTdBottom } from "../../../components/ReactElement/OriginalLabel/LabelFunction"

export function ChangeWorld() {
  // ================監控區==================
  let context = document.getElementsByClassName("context")[0];
  let ReconciliationTitle = ["排序", "日期", "店家", "商家代碼", "發票號碼", "操作"];
  let SalesTitle = ["日期", "分店", "營業額", "折扣額", "實收金額", "操作"];
  let SalesReportTitle = ["品項", "數量", "單價", "總額", "折扣", "實收金額"];
  let RightBtn = document.getElementsByClassName("RightBtn")[0];
  let ChildTitle0 = document.getElementsByClassName("ChildTitle0")[0];
  let ChildTitle5 = document.getElementsByClassName("ChildTitle5")[0];
  // 對帳單表格
  let clickMeSpan0 = document.getElementsByClassName("clickMeSpan0");
  let clickMeText0 = document.getElementsByClassName("clickMeText0");
  let clickMeText5 = document.getElementsByClassName("clickMeText5");
  let ChildTd1 = document.getElementsByClassName("ChildTd1");
  let ChildTd2 = document.getElementsByClassName("ChildTd2");
  let ChildTd3 = document.getElementsByClassName("ChildTd3");
  let ChildTd4 = document.getElementsByClassName("ChildTd4");
  let ChildTd5 = document.getElementsByClassName("ChildTd5");
  let ChildGroupTitle3 = document.getElementsByClassName("ChildGroupTitle3");
  let ChildGroupContent3 = document.getElementsByClassName("ChildGroupContent3");
  let ChildGroupTitle4 = document.getElementsByClassName("ChildGroupTitle4");
  let ChildGroupContent4 = document.getElementsByClassName("ChildGroupContent4");
  let ChildGroupTitle5 = document.getElementsByClassName("ChildGroupTitle5");
  let ChildGroupContent5 = document.getElementsByClassName("ChildGroupContent5");
  // 銷售報表上方表格
  let LabelTitleText = document.getElementsByClassName("LabelTitleText");
  let TitleText = document.getElementsByClassName("TitleText");
  let TopclickMeSpan0 = document.getElementsByClassName("TopclickMeSpan0");
  let TopclickMeText0 = document.getElementsByClassName("TopclickMeText0");
  let TopclickMeText5 = document.getElementsByClassName("TopclickMeText5");
  let TopChildTd1 = document.getElementsByClassName("TopChildTd1");
  let TopChildTd2 = document.getElementsByClassName("TopChildTd2");
  let TopChildTd3 = document.getElementsByClassName("TopChildTd3");
  let TopChildTd4 = document.getElementsByClassName("TopChildTd4");
  let TopChildGroupTitle3 = document.getElementsByClassName("TopChildGroupTitle3");
  let TopChildGroupContent3 = document.getElementsByClassName("TopChildGroupContent3");
  let TopChildGroupTitle4 = document.getElementsByClassName("TopChildGroupTitle4");
  let TopChildGroupContent4 = document.getElementsByClassName("TopChildGroupContent4");
  let TopChildGroupTitle5 = document.getElementsByClassName("TopChildGroupTitle5");
  let TopChildGroupContent5 = document.getElementsByClassName("TopChildGroupContent5");
  // 銷售報表下方表格
  let BottomclickMeSpan0 = document.getElementsByClassName("BottomclickMeSpan0");
  let BottomclickMeText0 = document.getElementsByClassName("BottomclickMeText0");
  let BottomChildTd1 = document.getElementsByClassName("BottomChildTd1");
  let BottomChildTd2 = document.getElementsByClassName("BottomChildTd2");
  let BottomChildTd3 = document.getElementsByClassName("BottomChildTd3");
  let BottomChildTd4 = document.getElementsByClassName("BottomChildTd4");
  let BottomChildTd5 = document.getElementsByClassName("BottomChildTd5");
  let BottomChildGroupTitle3 = document.getElementsByClassName("BottomChildGroupTitle3");
  let BottomChildGroupContent3 = document.getElementsByClassName("BottomChildGroupContent3");
  let BottomChildGroupTitle4 = document.getElementsByClassName("BottomChildGroupTitle4");
  let BottomChildGroupContent4 = document.getElementsByClassName("BottomChildGroupContent4");
  let BottomChildGroupTitle5 = document.getElementsByClassName("BottomChildGroupTitle5");
  let BottomChildGroupContent5 = document.getElementsByClassName("BottomChildGroupContent5");

  if (window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "Reconciliation"
  ) {
    // ================Label頁面微調CSS=======================
    RightBtn.style.display = "none";
    context.style.paddingBottom = "30px";
    ChildTitle0.style.paddingLeft = "10px";
    ChildTitle0.style.width = "100px";
    ChildTitle5.style.width = "280px";

    //表單標題文字引入
    for (let x = 0; x < ReconciliationTitle.length; x++) {
      LabelTitleText[x].innerHTML = ReconciliationTitle[x];
    }
    // 表單內容引入
    for (let i = 0; i < localStorage.MaxPageValue; i++) {
      ChildTd5[i].style.paddingLeft = "0px";
      clickMeText0[i].innerHTML = ReconciliationLabel.DataComponet[i]["Index"];
      ChildTd1[i].innerHTML = ReconciliationLabel.DataComponet[i]["Date"];
      ChildTd2[i].innerHTML = ReconciliationLabel.DataComponet[i]["StoreName"];
      ChildTd3[i].innerHTML = ReconciliationLabel.DataComponet[i]["Code"];
      ChildTd4[i].innerHTML = ReconciliationLabel.DataComponet[i]["BillNumber"];
      ReactDOM.render(<ReconciliationBtns />, clickMeText5[i]);
      ChildGroupTitle3[i].innerHTML = ReconciliationTitle[3];
      ChildGroupContent3[i].innerHTML = ReconciliationLabel.DataComponet[i]["Code"];
      ChildGroupTitle4[i].innerHTML = ReconciliationTitle[4];
      ChildGroupContent4[i].innerHTML = ReconciliationLabel.DataComponet[i]["BillNumber"];
      ChildGroupTitle5[i].innerHTML = ReconciliationTitle[5];
      ReactDOM.render(<ChildReconciliationBtns />, ChildGroupContent5[i]);
      ReactDOM.render(<ClickMe className="JustClickMe" onClick={() => { UnfoldTd(i) }} />, clickMeSpan0[i]);
    }
  }

  if (window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "SalesReport"
  ) {
    // ================Label頁面微調CSS=======================
    context.style.paddingBottom = "40px";
    ChildTitle0.style.width = "100px";
    ChildTitle0.style.padding = "3px 0";
    RightBtn.style.right = "3%"
    // ==================Sidebar選擇  列表頁、報表管理  表單內容引入====================
    if (localStorage.MaxPageValue === undefined) {
      localStorage.setItem("MaxPageValue", 10, 60000)
    }
    // 上方表單標題文字引入
    for (let x = 0; x < SalesTitle.length; x++) {
      LabelTitleText[x].innerHTML = SalesTitle[x];
    }
    // 下方表單標題文字引入
    for (let x = 0; x < SalesReportTitle.length; x++) {
      TitleText[x].innerHTML = SalesReportTitle[x];
    }
    //上方表單欄位內容引入
    for (let i = 0; i < localStorage.MaxPageValue; i++) {
      TopclickMeText0[i].innerHTML = Reconciliationdata.DataComponet[i]["Date"];
      TopChildTd1[i].innerHTML = Reconciliationdata.DataComponet[i]["Branch"];
      TopChildTd2[i].innerHTML = Reconciliationdata.DataComponet[i]["VolumeOfBusiness"];
      TopChildTd3[i].innerHTML = Reconciliationdata.DataComponet[i]["DiscountAmount"];
      TopChildTd4[i].innerHTML = Reconciliationdata.DataComponet[i]["Amount"];
      TopChildGroupContent5[i].style.width = "50%";
      ReactDOM.render(<DetailedBtn />, TopclickMeText5[i]);
      TopChildGroupTitle3[i].innerHTML = SalesTitle[3];
      TopChildGroupContent3[i].innerHTML = Reconciliationdata.DataComponet[i]["VolumeOfBusiness"];
      TopChildGroupTitle4[i].innerHTML = SalesTitle[4];
      TopChildGroupContent4[i].innerHTML = Reconciliationdata.DataComponet[i]["DiscountAmount"];
      TopChildGroupTitle5[i].innerHTML = SalesTitle[5];
      ReactDOM.render(<DetailedBtn />, TopChildGroupContent5[i]);
      ReactDOM.render(<ClickMe className="TopClickMe" onClick={() => { UnfoldTdTop(i) }} />, TopclickMeSpan0[i]);
    }
    //下方表單欄位內容引入
    for (let i = 0; i < localStorage.MaxPageValue; i++) {
      BottomclickMeText0[i].innerHTML = ReconciliationProductdata.DataComponet[i]["Product"];
      BottomChildTd1[i].innerHTML = ReconciliationProductdata.DataComponet[i]["Quantity"];
      BottomChildTd2[i].innerHTML = ReconciliationProductdata.DataComponet[i]["UnitPrice"];
      BottomChildTd3[i].innerHTML = ReconciliationProductdata.DataComponet[i]["TotalPrice"];
      BottomChildTd4[i].innerHTML = ReconciliationProductdata.DataComponet[i]["Discount"];
      BottomChildTd5[i].innerHTML = ReconciliationProductdata.DataComponet[i]["Amount"];
      BottomChildGroupTitle3[i].innerHTML = SalesReportTitle[3];
      BottomChildGroupContent3[i].innerHTML = ReconciliationProductdata.DataComponet[i]["TotalPrice"];
      BottomChildGroupTitle4[i].innerHTML = SalesReportTitle[4];
      BottomChildGroupContent4[i].innerHTML = ReconciliationProductdata.DataComponet[i]["Discount"];
      BottomChildGroupTitle5[i].innerHTML = SalesReportTitle[5];
      BottomChildGroupContent5[i].innerHTML = ReconciliationProductdata.DataComponet[i]["Amount"];
      ReactDOM.render(<ClickMe className="BottomClickMe" onClick={() => { UnfoldTdBottom(i) }} />, BottomclickMeSpan0[i]);
    }
  }
}
