import React, { useEffect } from "react";
import penIcon from "../../../images/svg/ui-icon/cil-pencil.svg"
import topArrew from "../../../images/svg/ui-icon/cil-arrow-top.svg"
// 引入函式
import { ArrangementChange } from "./LabelFunction"

// ===== 一般標題 =========
export function LabelTitleNormal() {
    let LabelTitleNormals = [];
    for (let x = 0; x < localStorage.LabelTitleCost; x++) {
        LabelTitleNormals.push(
            <th className={"ChildTitle ChildTitle" + x}
                style={{
                    color: "#2F353A",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "1px solid #C8CED3"
                }} key={x}><span className="LabelTitleText"></span><img
                    className="arrangementTop"
                    alt=""
                    onClick={() => {
                        ArrangementChange(x);
                    }}
                    style={{
                        with: "3px",
                        height: "10px",
                        marginTop: "5px",
                        marginRight: "1px",
                        cursor: "pointer",
                        float: "right"
                    }}
                    src={topArrew}></img></th>
        )
    }
    return <thead>
        <tr>
            {LabelTitleNormals}
        </tr>
    </thead>
}

// =====雙層標題======
export function LabelTitleTwin(props) {
    useEffect(() => {
        setTimeout(() => {
            UptownContent();
        }, 100);
    });
    let LabelTitleFake = [];
    let LabelTitleTwin = [];
    for (let x = 0; x < localStorage.LabelTitleCost; x++) {
        LabelTitleFake.push(
            <th className={"ChildTitleFake ChildTitleFake" + x}
                style={{
                    color: "#2F353A",
                    padding: "5px 0",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "none",
                    lineHeight: "40px"
                }} key={x}>
                {props.TitleText}
                <img alt=""
                    className="thisPenIcon"
                    src={penIcon}></img>
            </th>
        )
        LabelTitleTwin.push(
            <th className={"ChildTitle" + x}
                style={{
                    color: "#2F353A",
                    height: "40px",
                    fontSize: "14px",
                    fontWeight: "bolder",
                    backgroundColor: "#E7EBEF",
                    border: "1px solid #C8CED3"
                }} key={x}>
                <span className="LabelTitleText"></span><img
                    className={"arrangementTop"}
                    alt=""
                    onClick={() => {
                        ArrangementChange(x);
                    }}
                    style={{
                        with: "3px",
                        height: "10px",
                        marginTop: "6px",
                        marginRight: "3px",
                        cursor: "pointer",
                        float: "right"
                    }}
                    src={topArrew}></img>
            </th>
        )
    }
    return <thead>
        <tr>
            {LabelTitleFake}
        </tr>
        <tr>
            {LabelTitleTwin}
        </tr>
    </thead>
}

export function UptownContent() {
    let ChildTitleFake = document.getElementsByClassName("ChildTitleFake");
    let thisPenIcon = document.getElementsByClassName("thisPenIcon");
    for (let index = 0; index < ChildTitleFake.length; index++) {
        ChildTitleFake[index].style.color = "#E7EBEF";
        thisPenIcon[0].style.display = "initial";
        ChildTitleFake[0].style.color = "#2F353A";
        ChildTitleFake[0].style.paddingLeft = "10px"
    }
}
