import ReactDOM from 'react-dom';
import dayjs from 'dayjs';
import { WeatherApi } from "../../api/api";
import "../../styles/work/weather.css";
import { WhatDates } from "./weatherFunction";

// startTime: '2022-03-14 00:00:00', endTime: '2022-03-14 06:00:00'
// startTime: '2022-03-14 06:00:00', endTime: '2022-03-14 18:00:00'
// startTime: '2022-03-14 18:00:00', endTime: '2022-03-15 06:00:00'

let Today = new Date();
let TimeFrom = String();
let TimeTo = String();

export function Day12Hrs(day) {
    if (dayjs(day).format('hh') > 18) {
        TimeFrom = dayjs(day).format('YYYY-MM-DD') + "T06:00:00";
        TimeTo = dayjs(day).format('YYYY-MM-DD') + "T18:00:00";
    }
}

// ===============  氣象紀錄 ==================
// 晴時多雲 多雲時陰短暫雨 陰有雨
// 稍有寒意至舒適 寒冷

// ==============天氣區塊======================
let WeatherMap = async () => {
    let SelectArea = document.getElementsByClassName("SelectArea")[0];
    try {
        let Item = await WeatherApi(document.getElementsByClassName("SelectCitys")[0].value, TimeFrom, TimeTo);
        console.log(Item)
        let Citys = Item.data.records.locations[0].location.map((item, i) =>
            <option key={i} value={i}>{item.locationName}</option>);
        ReactDOM.render(
            <>{Citys}</>, SelectArea
        );

        setTimeout(() => {
            let MaxATs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[5].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>最高溫度:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{MaxATs}</>, document.getElementsByClassName("MaxAT")[0]
            );

            let MinTs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[8].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>最低溫度:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{MinTs}</>, document.getElementsByClassName("MinT")[0]
            );

            let POPs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[0].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>降雨機率:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{POPs}</>, document.getElementsByClassName("POP")[0]
            );

            let UVIs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[9].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>紫外線指數:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{UVIs}</>, document.getElementsByClassName("UVI")[0]
            );

            let Wxs = Item.data.records.locations[0].location[SelectArea.value].weatherElement[6].time.map((item, i) =>
                <div key={i} value={i}>
                    <h6>天氣現象:<span>{item.elementValue[0].value}</span></h6>
                </div>);
            ReactDOM.render(
                <>{Wxs}</>, document.getElementsByClassName("Wx")[0]
            );
        }, 500)

    } catch (error) {
        console.log("異常", error);
    }
}

export function Weather() {
    Day12Hrs(Today);
    setTimeout(() => {
        WeatherMap();
    }, 100);
    return <div>
        <div>
            <select className="SelectCitys" onChange={() => {
                WeatherMap();
            }}>
                <option value={"F-D0047-003"}>宜蘭縣</option>
                <option value={"F-D0047-051"}>基隆市</option>
                <option value={"F-D0047-063"}>台北市</option>
                <option value={"F-D0047-071"}>新北市</option>
                <option value={"F-D0047-007"}>桃園市</option>
                <option value={"F-D0047-055"}>新竹市</option>
                <option value={"F-D0047-011"}>新竹縣</option>
                <option value={"F-D0047-015"}>苗栗縣</option>
                <option value={"F-D0047-075"}>台中市</option>
                <option value={"F-D0047-023"}>南投縣</option>
                <option value={"F-D0047-019"}>彰化縣</option>
                <option value={"F-D0047-027"}>雲林縣</option>
                <option value={"F-D0047-059"}>嘉義市</option>
                <option value={"F-D0047-031"}>嘉義縣</option>
                <option value={"F-D0047-079"}>台南市</option>
                <option value={"F-D0047-067"}>高雄市</option>
                <option value={"F-D0047-035"}>屏東縣</option>
                <option value={"F-D0047-039"}>台東縣</option>
                <option value={"F-D0047-043"}>花蓮縣</option>
                <option value={"F-D0047-047("}>澎湖市</option>
                <option value={"F-D0047-083"}>連江縣</option>
                <option value={"F-D0047-087"}>金門縣</option>
            </select>
            <select className="SelectArea" onChange={() => {
                WeatherMap();
            }}></select>
        </div>

        <div className="WeatherGroup">
            <button onClick={() => {
                if (dayjs(document.getElementsByClassName("dates")[0].textContent).format('YYYY-MM-DD') !== dayjs(Today).format('YYYY-MM-DD')) {
                    WhatDates("Y");
                    Day12Hrs(dayjs(document.getElementsByClassName("dates")[0].textContent).subtract(1, 'day').format('YYYY-MM-DD'));
                    setTimeout(() => {
                        WeatherMap();
                    }, 200)
                }
            }}>{"<="}</button>
            <div>
                <div className="dates">{dayjs(Today).format("YYYY-MM-DD")}</div>
                <div className="MaxAT"></div>
                <div className="MinT"></div>
                <div className="POP"></div>
                <div className="UVI"></div>
                <div className="Wx"></div>
            </div>
            <button onClick={() => {
                WhatDates("T");
                Day12Hrs(dayjs(document.getElementsByClassName("dates")[0].textContent).subtract(1, 'day').format('YYYY-MM-DD'));
                setTimeout(() => {
                    WeatherMap();
                }, 200)
            }}>{"=>"}</button>
        </div>
    </div>
}
