import React, { useState } from "react";
import { Link } from "react-router-dom";
import { ReactComponent as CilPlus } from "../../images/svg/ui-icon/cil-plus.svg";
import { ReactComponent as CilReduce } from "../../images/svg/ui-icon/rwd減號/shadowLine.svg";

import "../../styles/ReactElement/TableRWD.scss";

export function ComponentTable({ data, dataOrder, dataHeard, operate }) {
  const [tableShowBtn, setTableShowBtn] = useState(false);
  const [btnImgChange, setBtnImgChange] = useState(false);
  const btnHandle = () => {
    const dataLength = dataOrder?.length;
    let btnClass = "";
    switch (dataLength) {
      case 3:
      case 4:
        btnClass = "d-sm-none";
        break;
      case 5:
        btnClass = "d-sm-none";
        break;
      case 6:
        btnClass = "d-md-none";
        break;
      default:
        btnClass = "";
        break;
    }
    return btnClass;
  };
  btnHandle();
  return (
    <div className="">
      <table className="table table-bordered border-light MMCtable">
        <tbody>
          <tr style={{ backgroundColor: "#E7EBEF" }}>
            <th scope="col" className="">
              {dataHeard && dataHeard[0]}
            </th>
            <th scope="col" className="">
              {dataHeard ? dataHeard[1] : ""}
            </th>
            <th scope="col" className="">
              {dataHeard ? dataHeard[2] : ""}
            </th>
            {dataHeard && dataHeard[3] && (
              <th scope="col" className="d-none d-sm-table-cell">
                {dataHeard[3]}
              </th>
            )}
            {dataHeard && dataHeard[4] && (
              <th scope="col" className="d-none d-sm-table-cell">
                {dataHeard[4]}
              </th>
            )}
            {dataHeard && dataHeard[5] && (
              <th scope="col" className="d-none d-md-table-cell ">
                {dataHeard[5]}
              </th>
            )}
            {dataHeard && dataHeard[6] && (
              <th scope="col" className="d-none d-xl-table-cell">
                {dataHeard[6]}
              </th>
            )}
          </tr>
          {data &&
            data.map((item) => {
              return (
                <>
                  <tr key={item.Index}>
                    {dataOrder.map((el, index) => {
                      let className = "";
                      switch (index) {
                        case 0:
                        case 1:
                        case 2:
                          break;
                        case 3:
                        case 4:
                          className = "d-none d-sm-table-cell";
                          break;
                        case 5:
                          className = "d-none d-md-table-cell";
                          break;
                        default:
                          className = "d-none d-xl-table-cell";
                          break;
                      }

                      if (el === "Button") {
                        return (
                          <td className={` ${className}`}>
                            {operate ? (
                              operate
                            ) : (
                              <div className="d-flex flex-wrap p-auto">
                                <button className="btn btn-primary text-white mx-1">
                                  編輯
                                </button>
                                <button className="btn btn-danger text-white mx-1">
                                  刪除
                                </button>
                              </div>
                            )}
                          </td>
                        );
                      } else if (el === "Index" || el === "State") {
                        return (
                          <td className="">
                            <div className="d-flex">
                              {tableShowBtn === item.Index ? (
                                <CilReduce
                                  className={`tableBtnImg btn btn-danger p-1 me-3 ${btnHandle()}`}
                                  fill="#000"
                                  onClick={(e) => {
                                    setBtnImgChange(!btnImgChange);
                                    setTableShowBtn(
                                      tableShowBtn === item.Index
                                        ? ""
                                        : item.Index
                                    );
                                  }}
                                />
                              ) : (
                                <CilPlus
                                  className={`tableBtnImg btn btn-primary me-3 ${btnHandle()}`}
                                  fill="#fff"
                                  onClick={(e) => {
                                    setBtnImgChange(!btnImgChange);
                                    setTableShowBtn(
                                      tableShowBtn === item.Index
                                        ? ""
                                        : item.Index
                                    );
                                  }}
                                />
                              )}
                              <p className="">{item.Index || item.State}</p>
                            </div>
                          </td>
                        );
                      } else {
                        return <td className={className}>{item[el]}</td>;
                      }
                    })}
                  </tr>
                  {+tableShowBtn === item.Index ? (
                    <tr
                      className={`border w-100 `}
                      style={{ borderRightWidth: "0px" }}
                    >
                      <td
                        colSpan="3"
                        className="p-0"
                        style={{ borderRightWidth: "0px" }}
                      >
                        {dataOrder.map((el, index) => {
                          let className = "";
                          switch (index) {
                            case 0:
                            case 1:
                            case 2:
                              className = "d-none";
                              break;
                            case 3:
                            case 4:
                              className = "d-sm-none";
                              break;
                            case 5:
                              className = "d-md-none";
                              break;
                            default:
                              className = "d-xl-none";
                              break;
                          }

                          if (el === "Button") {
                            return (
                              <div
                                className={`d-flex justify-content-between px-3 py-2 align-items-center ${className}`}
                              >
                                <div>
                                  <p
                                    style={{
                                      fontSize: "14px",
                                      fontWeight: "bold",
                                    }}
                                  >
                                    {dataHeard[index]}
                                  </p>
                                </div>
                                {operate ? (
                                  operate
                                ) : (
                                  <div>
                                    <button className="btn btn-primary text-white me-3">
                                      編輯
                                    </button>
                                    <button className="btn btn-danger text-white">
                                      刪除
                                    </button>
                                  </div>
                                )}
                              </div>
                            );
                          } else {
                            return (
                              <div
                                className={`d-flex justify-content-between w-100 px-3 py-2 border-bottom ${className}`}
                              >
                                <div className={className}>
                                  <p
                                    style={{
                                      fontSize: "14px",
                                      fontWeight: "bold",
                                    }}
                                  >
                                    {dataHeard[index]}
                                  </p>
                                </div>
                                <div className={className}>{item[el]}</div>
                              </div>
                            );
                          }
                        })}
                      </td>
                    </tr>
                  ) : (
                    ""
                  )}
                </>
              );
            })}
        </tbody>
      </table>
    </div>
  );
}

export function ComponentPage() {
  return (
    <div className="d-flex justify-content-between">
      <p className="my-5">Showing 1 to 10 of 32 entries</p>
      <nav aria-label="Page navigation example me-auto ">
        <ul className="pagination my-4">
          <li className="page-item">
            <Link to="#" className="page-link" href="#">
              Previous
            </Link>
          </li>
          <li className="page-item">
            <Link to="#" className="page-link" href="#">
              1
            </Link>
          </li>
          <li className="page-item">
            <Link to="#" className="page-link" href="#">
              2
            </Link>
          </li>
          <li className="page-item">
            <Link to="#" className="page-link" href="#">
              3
            </Link>
          </li>
          <li className="page-item">
            <Link to="#" className="page-link" href="#">
              Next
            </Link>
          </li>
        </ul>
      </nav>
    </div>
  );
}
