import React, { useEffect } from "react";
import { ProgressBar } from "../../../components/ReactElement/ProgressBar"
import { LabelSelect } from "../../../components/ReactElement/InputCombo"
import { ColorBtn } from "../../../components/ReactElement/ColorBtn"
import { MealStatementLabel} from "./PutLabel"
import "../../../styles/Put/sitting.css"
import { PutChangeWorld } from "./LabelFunction"
import { BreadcrumbSwitch } from "../../../components/ReactElement/Breadcrumb"

export function PutSitting() {
    localStorage.setItem("LabelTitleCost", 9, 60000)  //製造第一個表單標題格數
    if (localStorage.MaxPageValue === undefined) {
        localStorage.setItem("MaxPageValue", 10, 60000);
    }
    if (localStorage.ShowManAll === undefined) {
        localStorage.setItem("ShowManAll", 0, 6000);
    }
    localStorage.setItem("LabelCotentCost", localStorage.MaxPageValue, 60000) //製造表單欄位格數
    useEffect(() => {
        setTimeout(() => {
            BreadcrumbSwitch();
            PutChangeWorld();
        }, 100);
    });
    return <>

        <ProgressBar
            step1="上架現場餐點"
            step2="確認餐點結單"
            step3="備餐/補貨" />
        <div className="PutMachine">
            <LabelSelect p="主機" />
            <ColorBtn
                className="SearchBtn"
                marginLeft="20px"
                text="搜尋"
                backgroundColor="#1985AC"
            />
        </div>
        <div className="MealTimeText">
            <h4>供餐時間：2020/07/02 11:00~11:30</h4>
            <h6>至10:00前可回上一步重新設定現場餐點，確認日期、時段、品項，即可列印備餐。請在hh：mm前完成結單動作，逾時將取消訂單並通知消費者。</h6>
        </div>
        <MealStatementLabel />
    </>
}
