// import * as Exampledata from "../../../components/Json/ExampleLabel.json"
// import * as Putdata from "../../../components/Json/PutData.json"
// import ReactDOM from 'react-dom';
// 引入按鈕
// import { ClickMe, TwinBtns, ChildTwinBtns } from "../../../components/ReactElement/ColorBtn";
// 引入函式
// import { UnfoldTd } from "../../../components/ReactElement/OriginalLabel/LabelFunction"

export function PutChangeWorld() {
  // ================Label頁面微調CSS=======================
  let context = document.getElementsByClassName("context")[0];
  if (
    window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "menu") {
    context.style.paddingBottom = "50px";
  }
  let LabelTitleText = document.getElementsByClassName("LabelTitleText");
  // let clickMeSpan = document.getElementsByClassName("clickMeSpan");
  // let clickMeText = document.getElementsByClassName("clickMeText");
  // let ChildTitle0 = document.getElementsByClassName("ChildTitle0")[0];
  // let ChildTd0 = document.getElementsByClassName("ChildTd0")
  // let ChildTd1 = document.getElementsByClassName("ChildTd1");
  // let ChildTd2 = document.getElementsByClassName("ChildTd2");
  // let ChildTd3 = document.getElementsByClassName("ChildTd3");
  // let ChildTd4 = document.getElementsByClassName("ChildTd4");
  // let ChildTd5 = document.getElementsByClassName("ChildTd5");
  // let ChildTd6 = document.getElementsByClassName("ChildTd6");
  let ChildTd7 = document.getElementsByClassName("ChildTd7");
  let ChildTd8 = document.getElementsByClassName("ChildTd8");
  // let ChildGroupTitle3 = document.getElementsByClassName("ChildGroupTitle3");
  // let ChildGroupContent3 = document.getElementsByClassName("ChildGroupContent3");
  // let ChildGroupTitle4 = document.getElementsByClassName("ChildGroupTitle4");
  // let ChildGroupContent4 = document.getElementsByClassName("ChildGroupContent4");
  // let ChildGroupTitle5 = document.getElementsByClassName("ChildGroupTitle5");
  // let ChildGroupContent5 = document.getElementsByClassName("ChildGroupContent5");
  // let ChildGroupTitle6 = document.getElementsByClassName("ChildGroupTitle6");
  // let ChildGroupContent6 = document.getElementsByClassName("ChildGroupContent6");
  let ChildTitleFake7 = document.getElementsByClassName("ChildTitleFake7");
  let ChildTitleFake8 = document.getElementsByClassName("ChildTitleFake8");
  let ChildTitle7 = document.getElementsByClassName("ChildTitle7");
  let ChildTitle8 = document.getElementsByClassName("ChildTitle8");

  // ==================表單內容引入====================
  //  ====餐點結單====
  if (
    window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "Put" &&
    parseInt(localStorage.ShowManAll) === 0) {
    if (localStorage.MaxPageValue === undefined) {
      localStorage.setItem("MaxPageValue", 10, 60000)
    }
    // 表格消失
    for (let x = 0; x < 3; x++) {
      ChildTitleFake7[x].style.display = "none";
      ChildTitleFake8[x].style.display = "none";
      ChildTitle7[x].style.display = "none";
      ChildTitle8[x].style.display = "none";
    }
    for (let x = 0; x < ChildTd7.length; x++) {
      ChildTd7[x].style.display = "none";
      ChildTd8[x].style.display = "none";
    }
    // 表單標題文字引入
    for (let x = 0; x < LabelTitleText.length; x++) {
      if (x % parseInt(localStorage.LabelTitleCost) === 0) {
        LabelTitleText[x].style.marginLeft = "5px";
        LabelTitleText[x].textContent = "店家編號";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 1) {
        LabelTitleText[x].textContent = "店家";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 2) {
        LabelTitleText[x].textContent = "商品編號";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 3) {
        LabelTitleText[x].textContent = "商品名稱";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 4) {
        LabelTitleText[x].textContent = "預定";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 5) {
        LabelTitleText[x].textContent = "現場";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 6) {
        LabelTitleText[x].textContent = "操作";
      }
    }
  }
  // ====餐點時段設定====
  if (
    window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "Put" &&
    parseInt(localStorage.ShowManAll) === 1) {
    if (localStorage.MaxPageValue === undefined) {
      localStorage.setItem("MaxPageValue", 10, 60000)
    }
    // 表格消失
    for (let x = 0; x < 3; x++) {
      ChildTitleFake7[x].style.display = "block";
      ChildTitleFake8[x].style.display = "block";
      ChildTitle7[x].style.display = "block";
      ChildTitle8[x].style.display = "block";
    }
    for (let x = 0; x < ChildTd7.length; x++) {
      if (document.body.clientWidth > 768) {
        ChildTd7[x].style.display = "table-cell";
        ChildTd8[x].style.display = "table-cell";
      }
    }
    // 表單標題文字引入
    for (let x = 0; x < LabelTitleText.length; x++) {
      if (x % parseInt(localStorage.LabelTitleCost) === 0) {
        LabelTitleText[x].textContent = " ";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 1) {
        LabelTitleText[x].textContent = "主機";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 2) {
        LabelTitleText[x].textContent = "商品編號";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 3) {
        LabelTitleText[x].textContent = "商品名稱";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 4) {
        LabelTitleText[x].textContent = "售價";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 5) {
        LabelTitleText[x].textContent = "星期";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 6) {
        LabelTitleText[x].textContent = "指定日期";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 7) {
        LabelTitleText[x].textContent = "狀態";
      }
      if (x % parseInt(localStorage.LabelTitleCost) === 8) {
        LabelTitleText[x].textContent = "操作";
      }
    }
  }
}
