import React, { useState } from "react";
import "../../styles/Sidebar/Sidebar.css";
import "../../styles/ReactElement/ReactElement.css";
import fakeIcon from "../../../src/images/svg/ui-icon/fake.png";
import fiChevronRight from "../../../src/images/svg/ui-icon/fi_chevron-right.png";
import { ReactComponent as CilSpeedometer } from "../../../src/images/svg/ui-icon/cil-speedometer.svg";
import { ReactComponent as CilContact } from "../../../src/images/svg/ui-icon/cil-contact.svg";
import { ReactComponent as CilRestaurant } from "../../../src/images/svg/ui-icon/cil-restaurant.svg";
import { ReactComponent as CilNotes } from "../../../src/images/svg/ui-icon/cil-notes.svg";
import { ReactComponent as CilTruck } from "../../../src/images/svg/ui-icon/cil-truck.svg";
import { ReactComponent as CilAperture } from "../../../src/images/svg/ui-icon/cil-aperture.svg";
import { ReactComponent as CilMoney } from "../../../src/images/svg/ui-icon/cil-money.svg";
import { ReactComponent as CilChart } from "../../../src/images/svg/ui-icon/cil-chart-line.svg";
// 引入函式
import { RemoveSidebarOption,RemoveLabel } from "../ReactElement/RemoveLocalStorage"

export function Sidebar() {
  const [menuData] = useState([
    {
      Icon: <CilSpeedometer fill="#4F5D73" />,
      Name: "Dashboard",
      Child: [""],
      ChildState: ["none", "none"],
      OptionIcon: fakeIcon,
      Href: "Dashboard",
      ChildHref: [""],
    },
    // {
    //   Icon: <CilCursor fill="#4F5D73" />,
    //   Name: "Theme",
    //   Child: [""],
    //   OptionIcon: fakeIcon,
    //   Href: "Theme",
    //   ChildHref: [""],
    // },
    // {
    //   Icon: <Cil3d fill="#4F5D73" />,
    //   Name: "Button",
    //   Child: [""],
    //   OptionIcon: fakeIcon,
    //   Href: "Button",
    //   ChildHref: [""],
    // },
    // {
    //   Icon: <Cil3d fill="#4F5D73" />,
    //   Name: "Other",
    //   Child: [""],
    //   OptionIcon: fakeIcon,
    //   Href: "Other",
    //   ChildHref: [""],
    // },
    // {
    //   Icon: <CilNotes fill="#4F5D73" />,
    //   Name: "Form",
    //   Child: [""],
    //   OptionIcon: fakeIcon,
    //   Href: "Form",
    //   ChildHref: [""],
    // },
    // {
    //   Icon: <CilNotes fill="#4F5D73" />,
    //   Name: "列表頁",
    //   Child: ["一般列表", "Tab列表"],
    //   OptionIcon: fiChevronRight,
    //   Href: "label",
    //   ChildHref: ["/Date", "/Sorting"],
    // },
    // {
    //   Icon: <CilNotes fill="#4F5D73" />,
    //   Name: "內頁",
    //   Child: ["內頁-編輯頁", "內頁-檢視頁"],
    //   OptionIcon: fiChevronRight,
    //   Href: "inside",
    //   ChildHref: ["/Edit", "/View"],
    // },
    {
      Icon: <CilContact fill="#4F5D73" />,
      Name: "使用者管理",
      Child: ["店家管理", "群組與權限設定"],
      ChildState: ["block", "block"],
      OptionIcon: fiChevronRight,
      Href: "",
      ChildHref: ["store", "usergroups"],
    },
    {
      Icon: <CilRestaurant fill="#4F5D73" />,
      Name: "菜單管理",
      Child: ["菜單總覽", "餐點時段設定"],
      ChildState: ["block", "block"],
      OptionIcon: fiChevronRight,
      Href: "menu",
      ChildHref: ["/Overview", "/TimePeriod"],
    },
    {
      Icon: <CilRestaurant fill="#4F5D73" />,
      Name: "預訂/訂單管理",
      Child: [""],
      ChildState: ["none", "none"],
      OptionIcon: fakeIcon,
      Href: "Put",
      ChildHref: [""],
    },
    {
      Icon: <CilRestaurant fill="#4F5D73" />,
      Name: "訂單管理",
      Child: ["預定訂單"],
      ChildState: ["block", "none"],
      OptionIcon: fiChevronRight,
      Href: "OrderManagement",
      ChildHref: ["/create"],
    },
    {
      Icon: <CilTruck fill="#4F5D73" />,
      Name: "運補管理",
      Child: ["補貨單管理", "機台商品"],
      ChildState: ["block", "block"],
      OptionIcon: fiChevronRight,
      Href: "",
      ChildHref: ["replenishmentorder", "machinemerchandise"],
    },
    {
      Icon: <CilChart fill="#4F5D73" />,
      Name: "行銷管理",
      Child: [""],
      ChildState: ["none", "none"],
      OptionIcon: fakeIcon,
      Href: "marketing",
      ChildHref: [""],
    },
    {
      Icon: <CilAperture fill="#4F5D73" />,
      Name: "機器管理",
      Child: ["機台設定", "機器通知"],
      ChildState: ["block", "block"],
      OptionIcon: fiChevronRight,
      Href: "",
      ChildHref: ["machinesettings", "machinenotification"],
    },
    {
      Icon: <CilNotes fill="#4F5D73" />,
      Name: "報表管理",
      Child: ["對帳單", "銷售報表"],
      ChildState: ["block", "block"],
      OptionIcon: fiChevronRight,
      Href: "Report",
      ChildHref: ["/Reconciliation", "/SalesReport"],
    },
    {
      Icon: <CilMoney fill="#4F5D73" />,
      Name: "財務管理",
      Child: [""],
      ChildState: ["none", "none"],
      OptionIcon: fakeIcon,
      Href: "Finance",
      ChildHref: [""],
    },
  ]);
  return <div className="Localdiv">
    <a
      href="http://localhost:3500/"
      onClick={() => {
        RemoveSidebarOption();
        RemoveLabel();
      }}
    >
      <div className={"PopGoIcon"}></div>
    </a>
    <ul className={"sectionUl"}>
      {menuData.map((item) => {
        return (
          <div
            key={item["Name"]}
            href={item["href"]}
            className={"sectionBtnBox"}
          >
            <div className={"section"}>
              <div
                className={"sectionBtn"}
                onClick={() => {
                  RemoveSidebarOption();
                  RemoveLabel();
                  if (item["OptionIcon"] === fakeIcon) {
                    window.location.href =
                      "http://localhost:3500/" + item["Href"];
                  } else {
                    localStorage.setItem(
                      "SidebarIndex",
                      item["Name"],
                      600000
                    );
                  }
                  BtnSelect();
                }}
              >
                <div className="OptionSvgs">{item["Icon"]}</div>
                {item["Name"]}
                <span>
                  <img
                    alt=""
                    className="OptionIcon"
                    width="7px"
                    src={item["OptionIcon"]}
                  />
                </span>
              </div>
            </div>
            <div className={"sectionChild"}>
              <button
                value={item["Child"][0]}
                style={{
                  display: item["ChildState"][0]
                }}
                className={"sectionChilds"}
                onClick={() => {
                  localStorage.setItem(
                    "OptionIndex",
                    item["Child"][0],
                    600000
                  );
                  window.location.href =
                    "http://localhost:3500/" +
                    item["Href"] +
                    item["ChildHref"][0];
                }}
              >
                {item["Child"][0]}
              </button>
              <button
                value={item["Child"][1]}
                style={{
                  display: item["ChildState"][1]
                }}
                className={"sectionChilds"}
                onClick={() => {
                  localStorage.setItem(
                    "OptionIndex",
                    item["Child"][1],
                    600000
                  );
                  window.location.href =
                    "http://localhost:3500/" +
                    item["Href"] +
                    item["ChildHref"][1];
                }}
              >
                {item["Child"][1]}
              </button>
            </div>
          </div>
        );
      })}
    </ul>
  </div>
}

// Sidebar的按鈕選擇以及箭頭變化效果
function BtnSelect() {
  let sectionBtn = document.getElementsByClassName("sectionBtn");
  let sectionChild = document.getElementsByClassName("sectionChild");
  let OptionIcon = document.getElementsByClassName("OptionIcon");
  for (let x = 0; x < sectionBtn.length; x++) {
    if (sectionBtn[x].textContent === localStorage.SidebarIndex) {
      if (sectionChild[x].getElementsByTagName("button")[0].value.length > 0) {
        if (sectionChild[x].style.display === "") {
          sectionChild[x].style.display = "block";
          OptionIcon[x].style.transform = "rotate(" + 90 + "deg)";
        } else if (sectionChild[x].style.display === "block") {
          sectionChild[x].style.display = "none";
          OptionIcon[x].style.transform = "rotate(" + 0 + "deg)";
        } else if (sectionChild[x].style.display === "none") {
          sectionChild[x].style.display = "block";
          OptionIcon[x].style.transform = "rotate(" + 90 + "deg)";
        }
      }
    }
  }
}

// Sidebar的按鈕下的子項目變化效果
function ChildSelect() {
  let sectionChilds = document.getElementsByClassName("sectionChilds");
  for (let index = 0; index < sectionChilds.length; index++) {
    if (sectionChilds[index].textContent.length > 0) {
      if (sectionChilds[index].value === localStorage.OptionIndex) {
        sectionChilds[index].style.backgroundColor = "#092E65";
        sectionChilds[index].style.color = "white";
      } else {
        sectionChilds[index].style.backgroundColor = "#F1F5FB";
        sectionChilds[index].style.color = "#092E65";
      }
    }
  }
}


setTimeout(() => {
  BtnSelect();
  ChildSelect();
}, 100);
