/* eslint-disable react-hooks/exhaustive-deps */
// import React, { useState, useRef, useEffect } from "react";
import React, { useState, useEffect } from "react";
import { Button, Modal } from "react-bootstrap";
import "../../styles/ReactElement/EditRestaurant.scss";
// import SwitchButton from "../ReactElement/SwitchButton";
import QRimg from "../../images/images/png/1200px-QRcode_image.svg.png";

function ReplenishmentQR({
  buttonTitle,
  buttonClassName,
  modalTitle,
  enterColor,
  closeColor,
  removeColor,
  // setRoomProperty,
  // roomProperty,
}) {
  // const inputRef = useRef();

  // useEffect(() => {
  //   console.log(roomProperty);
  // }, roomProperty);
  // const [companyData, setCompanyDatas] = useState(null);
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  //店家資料
  // const [roomProperty, setRoomProperty] = useState([
  //   { id: "1", cname: "日式", state: "1" },
  //   { id: "2", cname: "韓式", state: "1" },
  //   { id: "3", cname: "燒烤", state: "1" },
  //   { id: "4", cname: "火鍋", state: "1" },
  //   { id: "5", cname: "美式", state: "1" },
  // ]);

  //10/26由這開始
  //建立空的清單...從localstorage去取得 並儲存到roomProperty
  // const [roomProperty, setRoomProperty] = useState([]);
  const [roomProperty, setRoomProperty] = useState([]);
  // const [rpError, setRpError] = useState({
  //   error: "",
  // });
  // console.log(roomProperty);
  const getLocalStorageData = () => {
    let local = JSON.parse(localStorage.getItem("roomProperty")) || [];
    setRoomProperty(local);
    console.log(roomProperty);
  };
  useEffect(() => {
    getLocalStorageData();
  }, []);

  //刪除單筆餐廳屬性

  //控制狀態
  const stateHandler = () => {};
  console.log(stateHandler);
  return (
    <>
      <Button variant={buttonClassName} onClick={handleShow}>
        {buttonTitle}
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            <h4 style={{ fontWeight: "bold" }}>{modalTitle}</h4>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="ReplenishmentTitle">
            <div className="text-center ReplenishmentTitleGroup">
              <div className="ReplenishmentTitleImg">
                <img src={QRimg} alt="" />
              </div>
              <h5>補貨編號:KM00145</h5>
            </div>
            <p>店家:新店大樓</p>
            <p>機台名稱:H101112045689</p>
            <p>機台地址:新北市新店區寶橋路188號</p>
            <div className="ReplenishmentTitleData">
              <p>2020-12-25 18:00 前完成補貨</p>
            </div>
          </div>
          <div className="editRestaurantBody">
            <table className="table table-bordered text-center">
              <tbody>
                <tr style={{ backgroundColor: "#e6ebef" }}>
                  <th scope="col">商品</th>
                  <th scope="col">數量</th>
                </tr>
                <tr>
                  <td>魔法阿罵</td>
                  <td>0</td>
                </tr>
                <tr>
                  <td>阿罵的腳皮</td>
                  <td>0</td>
                </tr>
              </tbody>
            </table>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant={removeColor} onClick={handleClose}>
            返回
          </Button>
          {enterColor && <Button variant={enterColor}>確認</Button>}
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ReplenishmentQR;
