import Swal from "sweetalert2";
import { ImInside, ImOut } from "./LoginEffect"

// ======================超過24小時則刪除==========================
(function OneDayOut() {
  let Today = new Date();
  let TodayValue = parseInt(String(Today.getFullYear()) + String(Today.getMonth()) + String(Today.getDate()) + String(Today.getHours()));
  let OnceUponATime = parseInt(localStorage.TKTime);
  if ((TodayValue - OnceUponATime) >= 100) {
    localStorage.removeItem("TK");
    localStorage.removeItem("TKTime");
    localStorage.removeItem("LoginUser");
    localStorage.removeItem("ShowManAll");
    localStorage.removeItem("trIndex");
  }
})();

// ==================超過30分鐘滑鼠沒動就要刪除Token==================
function NoMouseMove() {
  if (localStorage.StayHere === localStorage.StayHere) {
    localStorage.removeItem("TK");
    localStorage.removeItem("ShowManAll");
    localStorage.removeItem("trIndex");
  }
  setTimeout(NoMouseMove, 1800000);
}
setTimeout(NoMouseMove, 1800000);

// 滑鼠移動記錄
window.addEventListener('mousemove', (event) => {
  let Today = new Date();
  setTimeout(() => {
    localStorage.setItem("StayHere", `${event.pageX},${event.pageY}`);
    localStorage.setItem("TKTime", String(Today.getFullYear()) + String(Today.getMonth()) + String(Today.getDate()) + String(Today.getHours()), 5000);
  }, 100)
});

