    .then((response) => {
      return response.json();
    })
    .then((res) => {
      if (res.status === false && window.location.href === "http://localhost:3500/menber/changePW") {

      }
      else if (res.status === false && window.location.href === "http://localhost:3500/menber/forgetPW") {

      }
      else if (res.status === false && window.location.href !== "http://localhost:3500/menber") {
        window.location.href = "http://localhost:3500/menber";
        ImOut();
      }
      else {
        ImInside();
      }
    });
})();

// 登入的取得API
export function LoginApi(mailVaule, passwordVaule) {
  let userData = {
    user_account: mailVaule,
    user_password: passwordVaule,
  };
  fetch("https://api-v1-t.hillcorp.com/cms2/account/login", {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },
    body: JSON.stringify(userData),
  })
    .then((response) => {
      return response.json();
    })
    .then((res) => {
      let Today = new Date();
      if (res.status === true) {
        localStorage.setItem("LoginMail", mailVaule);
        localStorage.setItem("LoginUser", "Mingo");
        localStorage.setItem("TK", res.data["accessToken"], 5000);
        localStorage.setItem("TKTime", String(Today.getFullYear()) + String(Today.getMonth()) + String(Today.getDate()) + String(Today.getHours()), 5000);
        window.location.href = "http://localhost:3500/loginSelect";
      } else {
        let AlertText = document.getElementsByClassName("AlertText")[0];
        AlertText.style.display = "block";
        AlertText.innerHTML = "密碼輸入錯誤";
      }
    });
}

