//添加店家資料頁面
import React, { useState } from "react";
import "../../styles/store/store.scss";
// import CustomInput from "../Store/StoreCustomInput";
//選擇檔案元件
// import EasyCrop from "../ReactElement/easyCrop";
// import SwitchButton from "../ReactElement/SwitchButton";

//測試用SWITCH
// import { Switch } from "antd";
// import { CloseOutlined, CheckOutlined } from "@ant-design/icons";
//下拉多選元件
import { Select } from "antd";
import nobg from "../../images/images/background/nobg.png";
function StoreData({
  storeError,
  contactCount,
  roomProperty,
  setRoomProperty,
  companyDataError,
  background,
  companyData,
  setCompanyData,
  handelChange,
  handleInput,
  id,
  setContactInfo,
  contactInfo,
}) {
  // const switchRef = useRef();
  // const deliveryRef = useRef();
  const { Option } = Select;
  const [same, setSame] = useState(false);
  function handleChange(value) {
    console.log(`selected ${value}`);
  }
  return (
    <div className="storeCreatGroup">
      <div className="storeCreatTextTitle">
        <p>新增店家資料</p>
      </div>
      <div className="storeCreatTextBody">
        <div className="storeCheck">
          <input
            type="checkbox"
            checked={same}
            onClick={() => {
              setSame(!same);
            }}
          />
          同公司資料
        </div>
        <div className="storeCreatTextItem" style={{ width: "85%" }}>
          <h6>狀態</h6>
          {/* 手切SwitchBtn */}
          <div className="switchToggle">
            <input
              className="switchOnOff"
              type="checkbox"
              name="state"
              id="switch"
              data-order={id}
              onChange={handleInput}
              defaultChecked={false}
            />
            <label htmlFor="switch">Toggle</label>
          </div>

          {/* <Switch
            checkedChildren="开启"
            unCheckedChildren="关闭"
            defaultChecked={companyData.storeState}
            onChange={handleInput}
            className="statata"
          />
          <SwitchButton
            handleInput={handleInput}
            switchRef={switchRef}
            companyData={companyData.storeState}
            setCompanyData={setCompanyData}
          /> */}
        </div>
        <div className="storeCreatTextItem col-12 col-xl-4 ">
          <h6>
            店家名稱<span style={{ color: "red" }}>*</span>
          </h6>
          <input
            data-order={id}
            type="text"
            placeholder="請輸入店家名稱"
            name="storeStoreHeading"
            onChange={handleInput}
            style={{
              borderColor: storeError.storeStoreHeading ? "red" : "",
            }}
            required
          />
          {storeError.storeStoreHeading && (
            <span style={{ color: "red" }}>請輸入店家名稱</span>
          )}
        </div>
        <div className="storeCreatTextItem col-12 col-xl-4 ">
          <h6>店家編號</h6>
          <input
            data-order={id}
            type="text"
            placeholder="請輸入店家編號"
            name="storeStoreNumbers"
            onChange={handleInput}
            required
          />
        </div>
        <div className="storeCreatTextItem col-12 col-xl-4 ">
          <h6>店家統編</h6>
          <input
            data-order={id}
            type="text"
            placeholder="請輸入店家統編"
            name="storeStoreUNumbers"
            onChange={handleInput}
            required
          />
        </div>

        <div className="storeCreatTextItem" style={{ width: "100%" }}>
          <h6>
            店家簡述<span style={{ color: "red" }}>*</span>
          </h6>
          <textarea
            data-order={id}
            placeholder="請輸入內容"
            name="storeStoreText"
            onChange={(e) => {
              handleInput(e);
            }}
            required
            style={{ borderColor: storeError.storeStoreText ? "red" : "" }}
            // value={companyData.storeStoreText}
          ></textarea>
          {storeError.storeStoreText && (
            <span style={{ color: "red" }}>請輸入店家簡述</span>
          )}
        </div>
        {/* <EasyCrop /> */}
        <div className="storeCreatImgRow">
          <div className="storeCreatImgGroup">
            <p>APP店家主圖</p>
            <input
              type="file"
              accept="image/png, image/jpeg"
              onChange={handleInput}
              data-order={id}
              name="storeStoreOwnerLogo"
            />
            {contactInfo[id]?.storeStoreOwnerLogo ? (
              <div
                className="w-100 d-flex storeImg"
                style={{ backgroundColor: "#F0F3F5" }}
              >
                <img
                  className="w-100"
                  src={contactInfo[id].storeStoreOwnerLogo}
                  alt=""
                />
              </div>
            ) : (
              <div
                className="w-100 d-flex"
                style={{ backgroundColor: "#F0F3F5", height: "140px" }}
              >
                <img className="w-25" src={nobg} alt="" />
              </div>
            )}
          </div>
          <div className="storeCreatImgGroup">
            <p>機台上LOGO</p>
            <input
              type="file"
              accept="image/png, image/jpeg"
              onChange={handleInput}
              data-order={id}
              name="storeStoreMachineLogo"
            />
            {contactInfo[id]?.storeStoreMachineLogo ? (
              <div
                className="w-100 d-flex"
                style={{ backgroundColor: "#F0F3F5" }}
              >
                <img
                  className="w-100"
                  src={contactInfo[id].storeStoreMachineLogo}
                  alt=""
                />
              </div>
            ) : (
              <div
                className="w-100 d-flex"
                style={{ backgroundColor: "#F0F3F5", height: "140px" }}
              >
                <img className="w-25" src={nobg} alt="" />
              </div>
            )}
          </div>
          <div className="storeCreatImgGroup">
            <p>APP店家LOGO</p>
            <input
              type="file"
              accept="image/png, image/jpeg"
              onChange={handleInput}
              data-order={id}
              name="storeStoreAppLogo"
            />
            {contactInfo[id]?.storeStoreAppLogo ? (
              <div
                className="w-100 d-flex"
                style={{ backgroundColor: "#F0F3F5" }}
              >
                <img
                  className="w-100"
                  src={contactInfo[id].storeStoreAppLogo}
                  alt=""
                />
              </div>
            ) : (
              <div
                className="w-100 d-flex"
                style={{ backgroundColor: "#F0F3F5", height: "140px" }}
              >
                <img className="w-25" src={nobg} alt="" />
              </div>
            )}
          </div>
          {/* 裁切圖片功能BUG暫時移除 */}
          {/* <div className="storeCreatImgGroup">
              <p>APP店家主圖</p>
              <EasyCrop
                contactInfo={contactInfo}
                handleInput={handleInput}
                order={id}
                setContactInfo={setContactInfo}
                companyData={companyData}
                setCompanyData={setCompanyData}
                inputName={"storeStoreUNumbers"}
                smallText={"建議尺寸900px * 330px"}
                cropSize={{ width: 900, height: 330 }}
              />
            </div>
            <div className="storeCreatImgGroup">
              <p>機台上LOGE</p>
              <EasyCrop
                smallText={"建議尺寸300px * 100px"}
                cropSize={{ width: 300, height: 100 }}
              />
            </div>
            <div className="storeCreatImgGroup">
              <p>APP店家LOGO</p>
              <EasyCrop
                smallText={"500px * 500px"}
                cropSize={{ width: 500, height: 500 }}
              />
            </div> */}
        </div>
        <div className="d-flex col-12 my-3">
          {/* 下拉多選套件 */}
          <div className=" col-4 mx-3">
            <p>可使用功能</p>
            <Select
              className="canuser"
              data-order={id}
              mode="multiple"
              name="canuser"
              allowClear
              style={{ width: "100%" }}
              placeholder="請選擇"
              // defaultValue={["a10", "c12"]}
              onChange={handleChange}
            >
              <Option key="1" value="外帶">
                外帶
              </Option>
              <Option key="2" value="櫃取">
                櫃取
              </Option>
            </Select>
          </div>
          <div className="col-7 mx-3">
            <p>可使用機台</p>
            <Select
              data-order={id}
              mode="multiple"
              allowClear
              style={{ width: "100%" }}
              placeholder="請選擇"
              // defaultValue={["a10", "c12"]}
              onChange={handleChange}
            >
              {/* {children} */}
              <Option key="1" value="A1">
                A1
              </Option>
              <Option key="2" value="A2">
                A2
              </Option>
              <Option key="3" value="A3">
                A3
              </Option>
            </Select>
          </div>
        </div>

        {/* 餐廳屬性 */}
        <div className="storeCreatTextItem storeCreatTextItemCheck border-bottom">
          <h6>餐廳屬性</h6>
          <div className="storeCreatTextItemCheckGroup">
            {roomProperty &&
              roomProperty.map((item, index) => (
                <div key={item.id} className="storeCreatTextItemCheckGroupItem">
                  <input
                    data-order={id}
                    className="storeCreatTextItemCheckInput"
                    type="checkbox"
                    value={item.cname}
                    name="storeStoreAttr"
                    onClick={handleInput}
                    checked={contactInfo.storeStoreAttr?.[index]}
                    // style={{ width: "auto" }}
                  />
                  <label>{item.cname}</label>
                </div>
              ))}
          </div>
        </div>
        <div className="storeCreatTextItem col-12 col-xl-4 ">
          <h6>聯絡人姓名</h6>
          <input
            data-order={id}
            type="text"
            placeholder="請輸入聯絡人姓名"
            name="storeStoreName"
            onChange={handleInput}
            required
          />
        </div>
        <div className="storeCreatTextItem col-12 col-xl-4 ">
          <h6>聯絡人電話</h6>
          <input
            data-order={id}
            type="text"
            placeholder="請輸入聯絡人電話"
            name="storeStoreCell"
            onChange={handleInput}
            required
          />
        </div>
        <div className="storeCreatTextItem col-12 col-xl-4 ">
          <h6>聯絡人信箱</h6>
          <input
            data-order={id}
            type="text"
            placeholder="請輸入聯絡人信箱"
            name="storeStoreEmail"
            onChange={handleInput}
            required
          />
        </div>

        <div className="storeCreatTextItem">
          <button
            className="btn btn-primary"
            onClick={() => {
              console.log(contactInfo);
              // console.log(contactInfo.contact_0.storeStoreHeading);
            }}
          >
            測試的console按鈕
          </button>
        </div>
        {/* <button className="storeCreatButtons" onClick={(e) => handleInput(e)}>
            新增第{contactCount + 1}筆店家資料
          </button> */}
      </div>
    </div>
  );
}

