import React, { useState, useRef, useEffect } from "react";
import { Button, Modal } from "react-bootstrap";
import "../../styles/ReactElement/EditRestaurant.scss";
import SwitchButton from "../ReactElement/SwitchButton";

function EditRestaurant({
  buttonTitle,
  buttonClassName,
  modalTitle,
  enterColor,
  closeColor,
  removeColor,
  // setRoomProperty,
  // roomProperty,
}) {
  const inputRef = useRef();

  // useEffect(() => {
  //   console.log(roomProperty);
  // }, roomProperty);
  // const [companyData, setCompanyDatas] = useState(null);
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  //店家資料
  // const [roomProperty, setRoomProperty] = useState([
  //   { id: "1", cname: "日式", state: "1" },
  //   { id: "2", cname: "韓式", state: "1" },
  //   { id: "3", cname: "燒烤", state: "1" },
  //   { id: "4", cname: "火鍋", state: "1" },
  //   { id: "5", cname: "美式", state: "1" },
  // ]);

  //10/26由這開始
  //建立空的清單...從localstorage去取得 並儲存到roomProperty
  const [roomProperty, setRoomProperty] = useState([]);
  // const [rpError, setRpError] = useState({
  //   error: "",
  // });
  const [rpError] = useState({
    error: "",
  });
  const getLocalStorageData = () => {
    let local = JSON.parse(localStorage.getItem("roomProperty")) || [];
    setRoomProperty(local);
  };
  useEffect(() => {
    getLocalStorageData();
  }, []);

  //刪除單筆餐廳屬性

  //控制狀態
  // const stateHandler = () => {};

  return (
    <>
      <Button variant={buttonClassName} onClick={handleShow}>
        {buttonTitle}
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            <h4 style={{ fontWeight: "bold" }}>{modalTitle}</h4>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="editRestaurantTitle">
            <p>
              職位權限名稱<span style={{ color: "red" }}>*</span>
            </p>
            <div className="editRestaurantTitleCreat">
              <div className="d-flex flex-column">
                <input
                  type="text"
                  placeholder="請輸入分類名稱"
                  ref={inputRef}
                  style={{ borderColor: rpError.error ? "#F85756" : "" }}
                />
                <span style={{ color: "red", fontSize: "12px" }}>
                  {rpError.error}
                </span>
              </div>
              <button className="btn btn-success text-white" onClick={(e) => {}}>
                建立
              </button>
            </div>
          </div>
          <div className="editRestaurantBody">
            <table className="table table-bordered text-center">
              <tbody>
                <tr style={{ backgroundColor: "#e6ebef" }}>
                  <th scope="col">排序</th>
                  <th scope="col">職位名稱</th>
                  <th scope="col">狀態</th>
                </tr>
                {roomProperty &&
                  roomProperty.map((item, i) => {
                    return (
                      <tr key={item.id}>
                        <td>{i + 1}</td>
                        <td>{item.cname}</td>
                        <td>
                          <SwitchButton
                            // companyData={roomProperty}
                            companyData={item.state}
                            setCompanyData={setRoomProperty}
                          />
                        </td>
                      </tr>
                    );
                  })}
              </tbody>
            </table>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant={removeColor} onClick={handleClose}>
            返回
          </Button>
          <Button variant={enterColor}>確認</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default EditRestaurant;
