import React, { useState } from "react";
import ReactDOM from 'react-dom';
import "../../styles/header.scss";
import { Sidebar } from "./Sidebar";
// 引入元件
import Swal from "sweetalert2";
import { ColorBtn } from "../../components/ReactElement/ColorBtn";
import ReactBsModal from "react-bootstrap/Modal";
import { ReactBsFormGroup } from "../../../src/pages/Menber/Menber";
// import { GiHamburgerMenu } from "react-icons/gi";
import { Drawer } from "antd";
// 引用圖片
import { ReactComponent as CliMenu } from "../../images/svg/ui-icon/cil-hamburger-menu.svg";
// import cilBell from "../../images/svg/ui-icon/cil-bell.svg";
import bellDot from "../../images/svg/ui-icon/bell-dot.svg";
import cilUser from "../../images/svg/ui-icon/cil-user.svg";
import cilPeople from "../../images/svg/ui-icon/cil-people.svg";
import cilAccountLogout from "../../images/svg/ui-icon/cil-account-logout.svg";

function Header(props) {
  const { setSideBarOnOff, sideBarOnOff } = props;
  // const [mobileHamburger, setMobileHamburger] = useState(false);
  const sideBarShow = () => {
    setSideBarOnOff(!sideBarOnOff);
  };
  const [visible, setVisible] = useState(false);
  const showDrawer = () => {
    setVisible(true);
  };
  const onClose = () => {
    setVisible(false);
  };
  setTimeout(() => {
    // 小鈴鐺中的選項
    let BellArrey = ["上架餐點審核通過", "結單時間通知", "機器已回報"];
    let BellOptionsLan = document.getElementsByClassName("BellOptionsLan")[0];
    let BellIcon = document.getElementsByClassName("cmsHeaderImg")[0];
    let BellOptionLists = BellArrey.map((BellArrey,i)=> <h6 key={i} className="BellOption">{BellArrey}</h6>
    );
  if (BellArrey.length > 1) {
    BellIcon.src = "/static/media/bell-dot.cfcff14d.svg";
    ReactDOM.render(<>{ BellOptionLists }</>, BellOptionsLan);
  } if (BellArrey.length === 1) {
    BellIcon.src = "/static/media/cil-bell.953a478d.svg"
    ReactDOM.render(<>{"無通知"}</>, BellOptionsLan)
  }
}, 1000);

return <div className="title">
  <div className="CainAbelNone" onClick={() => {
    if (CainShow === false) {
      Cain();
    }
    if (AbelShow === false) {
      Abel();
    }
  }}>點我就關閉ICONS</div>
  <div className="titleGroup">
    <div className="titleIcon hamburger-web">
      <div className="titleIconImg">
        <CliMenu
          fill="#4F5D73"
          onClick={(e) => {
            sideBarShow(e);
          }}
        />
      </div>
    </div>
    <div className="titleIcon hamburger-mobile">
      <div className="titleIconImg">
        <CliMenu
          fill="#4F5D73"
          onClick={(e) => {
            showDrawer();
          }}
        />
      </div>
    </div>
    <Drawer
      bodyStyle={{ backgroundColor: "#f1f5fb", padding: "0" }}
      placement="left"
      onClose={onClose}
      visible={visible}
    >
      <Sidebar />
    </Drawer>
    <h1>{props.titleText}</h1>
  </div>
  <div className="titleMember">

    <div className="titleMemberSelects">
      <select name="" id="" placeholder="選擇廠商">
        <option value="">台灣中華電信股份有限公司</option>
      </select>
      <select name="" id="">
        <option value="">ALL</option>
      </select>
    </div>

    <div className="titleMemberIcons">
      <div onClick={() => {
        Cain();
      }} className="cmsHeaderIcon">
        <img className="cmsHeaderImg" src={bellDot} alt="" />
      </div>
      <div className="TinkerbellOption">
        <h5>通知</h5>
        <span className="BellOptionsLan">
        </span>
      </div>
      <div onClick={() => {
        Abel();
      }} className="cmsHeaderIcon">
        <img className="cmsHeaderImg" src={cilUser} alt="" />
        <h6 className="LoginName">{localStorage.LoginUser.split(",")[1]}</h6>
      </div>
      <div className="InvisibleOption">
        <h5>帳號</h5>
        <SelectCompanyId />
        <h6 onClick={() => {
          localStorage.setItem("urlLog", window.location.href, 6000)
          window.location.href = "http://localhost:3500/loginSelect"
        }}>
          <span>
            <img alt="" src={cilPeople}></img>
          </span>
          切換使用者身分
        </h6>
        <h6 onClick={() => {
          localStorage.removeItem("TK");
          localStorage.removeItem("TKTime");
          localStorage.removeItem("LoginUser");
          window.location.href = "http://localhost:3500/menber"
        }}>
          <span>
            <img alt="" src={cilAccountLogout}></img>
          </span>
          登出
        </h6>
      </div>
    </div>

  </div>
</div>
}
export default Header;

// ===============點選右上ICON出現Option效果=============
let CainShow = true;
let AbelShow = true;
export function Cain() {
  let TinkerbellOption = document.getElementsByClassName("TinkerbellOption")[0];
  let InvisibleOption = document.getElementsByClassName("InvisibleOption")[0];
  let CainAbelNone = document.getElementsByClassName("CainAbelNone")[0];
  CainShow = !CainShow;
  InvisibleOption.style.display = "none";
  CainAbelNone.style.display = CainShow ? "none" : "block";
  TinkerbellOption.style.display = CainShow ? "none" : "block";
}

export function Abel() {
  let TinkerbellOption = document.getElementsByClassName("TinkerbellOption")[0];
  let InvisibleOption = document.getElementsByClassName("InvisibleOption")[0];
  let CainAbelNone = document.getElementsByClassName("CainAbelNone")[0];
  AbelShow = !AbelShow;
  TinkerbellOption.style.display = "none";
  CainAbelNone.style.display = AbelShow ? "none" : "block";
  InvisibleOption.style.display = AbelShow ? "none" : "block";
}

//================選擇公司/店家視窗效果====================
export function SelectCompanyId(props) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  return (
    <>
      <h6 variant="primary" onClick={handleShow}>
        <span>
          <img alt="" src={cilUser}></img>
        </span>
        修改個人資訊
      </h6>
      <ReactBsModal show={show} onHide={handleClose}>
        <ReactBsModal.Header closeButton>
          <ReactBsModal.Title className="ModalTitle">
            修改個人資訊
          </ReactBsModal.Title>
        </ReactBsModal.Header>

        <div className="ChangeMe">
          <ReactBsFormGroup
            InputclassName="U"
            LabelContent="帳號"
            eyeDisplay="none"
            value={localStorage.LoginUser.split(",")[0]}
          />
          <ReactBsFormGroup
            InputclassName="U"
            LabelContent="姓名"
            inputType="text"
            eyeDisplay="none"
          />
          <ReactBsFormGroup
            InputclassName="U"
            LabelContent="原密碼"
            eyeDisplay="none"
            inputType="password"
          />
          <ReactBsFormGroup
            InputclassName="U"
            LabelContent="新密碼"
            eyeDisplay="none"
            inputType="password"
          />
          <ReactBsFormGroup
            InputclassName="U"
            LabelContent="再次輸入密碼"
            inputType="password"
            eyeDisplay="none"
          />
        </div>
        <hr className="ChangeMeHr" />
        <footer
          style={{
            display: "flex",
            paddingBottom: "40px",
          }}
        >
          <div
            className="backBtns"
            style={{
              marginTop: "-5px",
            }}
          >
            <ColorBtn
              className="BackBtn"
              text="返回"
              backgroundColor="#C8CED3"
              variant="primary"
              onClick={handleClose}
            />
            <ColorBtn
              className="PassBtn"
              text="確認"
              color="white"
              backgroundColor="#00BFA5"
              onClick={() => {
                setTimeout(() => {
                  Swal.fire("個人資料編輯成功", " ", "success");
                }, 500);
              }}
            />
          </div>
        </footer>
      </ReactBsModal>
    </>
  );
}
