import * as Exampledata from "../../../components/Json/ExampleLabel.json"
import * as Menudata from "../../../components/Json/MenuLabel.json"
// import * as Putdata from "../../../components/Json/PutData.json"
import * as Reconciliationdata from "../../../components/Json/ReconciliationLabel.json"
import ReactDOM from 'react-dom';
// 引入按鈕
import { ClickMe, TwinBtns, ChildTwinBtns, TripletsBtns, ChildTripletsBtns, ReconciliationBtns } from "../../../components/ReactElement/ColorBtn";
import { SwitchBtn } from "../../../components/ReactElement/SwitchBtn"
// 圖片引入
import convenient from "../../../images/images/png/便當.png"
// 引入函式
import { UnfoldTd } from "../../../components/ReactElement/OriginalLabel/LabelFunction"

export function ChangeWorld() {
  // ================Label頁面微調CSS=======================
  let context = document.getElementsByClassName("context")[0];
  if (
    window.location.href.split("http://localhost:3500/")[1].split("/")[0] === "label" ||
    window.location.href.split("http://localhost:3500/")[1].split("/")[0] === "menu" ||
    window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "Reconciliation" ||
    window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "SalesReport"
  ) {
    context.style.paddingBottom = "50px";
  }
  let RightBtn = document.getElementsByClassName("RightBtn")[0];
  let LabelTitleText = document.getElementsByClassName("LabelTitleText");
  let clickMeSpan = document.getElementsByClassName("clickMeSpan");
  let clickMeText = document.getElementsByClassName("clickMeText");
  let ChildTitle0 = document.getElementsByClassName("ChildTitle0")[0];
  let ChildTd0 = document.getElementsByClassName("ChildTd0")
  let ChildTd1 = document.getElementsByClassName("ChildTd1");
  let ChildTd2 = document.getElementsByClassName("ChildTd2");
  let ChildTd3 = document.getElementsByClassName("ChildTd3");
  let ChildTd4 = document.getElementsByClassName("ChildTd4");
  let ChildTd5 = document.getElementsByClassName("ChildTd5");
  let ChildTd6 = document.getElementsByClassName("ChildTd6");
  let ChildGroupTitle3 = document.getElementsByClassName("ChildGroupTitle3");
  let ChildGroupContent3 = document.getElementsByClassName("ChildGroupContent3");
  let ChildGroupTitle4 = document.getElementsByClassName("ChildGroupTitle4");
  let ChildGroupContent4 = document.getElementsByClassName("ChildGroupContent4");
  let ChildGroupTitle5 = document.getElementsByClassName("ChildGroupTitle5");
  let ChildGroupContent5 = document.getElementsByClassName("ChildGroupContent5");
  let ChildGroupTitle6 = document.getElementsByClassName("ChildGroupTitle6");
  let ChildGroupContent6 = document.getElementsByClassName("ChildGroupContent6");

  // ==================Sidebar選擇  列表頁、使用者管理  表單內容引入====================
  if (
    window.location.href.split("http://localhost:3500/")[1].split("/")[0] === "label" ||
    window.location.href.split("http://localhost:3500/")[1].split("/")[0] === "store") {

    let DataTitle = ["排序", "訂單編號", "銷售機器", "訂購類型", "金額", "付款方式", "操作"]
    if (localStorage.MaxPageValue === undefined) {
      localStorage.setItem("MaxPageValue", 10, 60000)
    }
    ChildTitle0.style.width = "50px";
    ChildTitle0.style.textAlign = "center";
    ChildTitle0.style.padding = "3px 0";
    if (window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "Sorting") {
      let paginationThree = document.getElementsByClassName("paginationThree")[0];
      paginationThree.style.display = "block";
      let paginationGroup = document.getElementsByClassName("paginationGroup");
      paginationGroup[1].style.height = "31px";
      paginationGroup[2].style.height = "31px";
    }

    // 表單標題文字引入
    for (let x = 0; x < DataTitle.length; x++) {
      LabelTitleText[x].innerHTML = DataTitle[x];
    }
    //表單欄位內容引入
    for (let i = 0; i < localStorage.MaxPageValue; i++) {
      clickMeText[i].innerHTML = Exampledata.DataCompent[i]["Index"];
      ChildTd1[i].innerHTML = Exampledata.DataCompent[i]["Number"];
      ChildTd2[i].innerHTML = Exampledata.DataCompent[i]["Machine"];
      ChildTd3[i].innerHTML = Exampledata.DataCompent[i]["Buyer"];
      ChildTd4[i].innerHTML = Exampledata.DataCompent[i]["Amount"];
      ChildTd5[i].innerHTML = Exampledata.DataCompent[i]["Type"];
      ChildGroupTitle3[i].innerHTML = DataTitle[3];
      ChildGroupContent3[i].innerHTML = Exampledata.DataCompent[i]["Buyer"];
      ChildGroupTitle4[i].innerHTML = DataTitle[4];
      ChildGroupContent4[i].innerHTML = Exampledata.DataCompent[i]["Amount"];
      ChildGroupTitle5[i].innerHTML = DataTitle[5];
      ChildGroupContent5[i].innerHTML = Exampledata.DataCompent[i]["Type"];
      ChildGroupTitle6[i].innerHTML = DataTitle[6];
      ReactDOM.render(<ChildTwinBtns />, ChildGroupContent6[i]);
      ReactDOM.render(<ClickMe onClick={() => { UnfoldTd(i) }} />, clickMeSpan[i]);
      ReactDOM.render(<TwinBtns />, ChildTd6[i]);
    }
  }
  // ==================Sidebar選擇  菜單管理  表單內容引入====================
  if (
    window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "Overview") {
    let MenuDataTitle = ["商品名稱", "訂單編號", "BarCode", "價格", "主圖", "狀態", "操作"];
    if (localStorage.MaxPageValue === undefined) {
      localStorage.setItem("MaxPageValue", 10, 60000)
    }
    ChildTitle0.style.width = "190px";
    ChildTitle0.style.paddingLeft = "10px";

    // ==============表單標題內容引入===============
    for (let x = 0; x < MenuDataTitle.length; x++) {
      LabelTitleText[x].innerHTML = MenuDataTitle[x];
    }
    // ==============表單欄位內容引入===============
    for (let i = 0; i < localStorage.MaxPageValue; i++) {
      clickMeText[i].style.paddingLeft = "10px";
      ChildTd0[i].style.textAlign = "left";
      clickMeText[i].innerHTML =
        Menudata.DataComponet[i]["Name"] +
        '<span className="ApprovalStyle">' +
        Menudata.DataComponet[i]["State"] +
        '</span>';
      ChildTd1[i].innerHTML = Menudata.DataComponet[i]["Number"];
      ChildTd2[i].innerHTML = Menudata.DataComponet[i]["Barcode"];
      ChildTd3[i].innerHTML = Menudata.DataComponet[i]["Amount"];
      ChildTd4[i].innerHTML = Menudata.DataComponet[i]["Pic"];
      ReactDOM.render(<SwitchBtn />, ChildTd5[i]);
      ReactDOM.render(<ChildTripletsBtns
        type={Menudata.DataComponet[i]["Type"]}
      />, ChildGroupContent6[i]);
      ChildGroupTitle3[i].innerHTML = MenuDataTitle[3];
      ChildGroupContent3[i].innerHTML = Menudata.DataComponet[i]["Amount"];
      ChildGroupTitle4[i].innerHTML = MenuDataTitle[4];
      ChildGroupContent4[i].innerHTML = Menudata.DataComponet[i]["Pic"];
      ChildGroupTitle5[i].innerHTML = MenuDataTitle[5];
      ReactDOM.render(<SwitchBtn />, ChildGroupContent5[i]);
      ChildGroupTitle6[i].innerHTML = MenuDataTitle[6];
      ReactDOM.render(<ClickMe onClick={() => { UnfoldTd(i) }} />, clickMeSpan[i]);
      ReactDOM.render(<TripletsBtns
        type={Menudata.DataComponet[i]["Type"]}
        text={Menudata.DataComponet[i]["Name"]}
        indivoduce={Menudata.DataComponet[i]["Introduce"]}
        pic={convenient}
        Amount={Menudata.DataComponet[i]["Amount"]}
      />, ChildTd6[i]);
    }
  }

  // ==================Sidebar選擇  對帳單  表單內容引入====================
  if (
    window.location.href.split("http://localhost:3500/")[1].split("/")[1] === "Reconciliation") {
    let ReconciliationDataTitle = ["排序", "日期", "店家", "商家代碼", "發票號碼", "操作"];
    if (localStorage.MaxPageValue === undefined) {
      localStorage.setItem("MaxPageValue", 10, 60000)
    }
    RightBtn.style.display = "none";
    ChildTitle0.style.width = "90px";
    ChildTitle0.style.paddingLeft = "7px";

    // ==============表單標題內容引入===============
    for (let x = 0; x < ReconciliationDataTitle.length; x++) {
      LabelTitleText[x].innerHTML = ReconciliationDataTitle[x];
    }
    // ==============表單欄位內容引入===============
    for (let i = 0; i < localStorage.MaxPageValue; i++) {
      clickMeText[i].style.paddingLeft = "10px";
      ChildTd0[i].style.textAlign = "left";
      clickMeText[i].innerHTML =
        Reconciliationdata.DataComponet[i]["Index"];
      ChildTd1[i].innerHTML = Reconciliationdata.DataComponet[i]["Date"];
      ChildTd2[i].innerHTML = Reconciliationdata.DataComponet[i]["StoreName"];
      ChildTd3[i].innerHTML = Reconciliationdata.DataComponet[i]["Code"];
      ChildTd4[i].innerHTML = Reconciliationdata.DataComponet[i]["BillNumber"];
      ReactDOM.render(<ReconciliationBtns />, ChildTd5[i]);
      ChildGroupTitle3[i].innerHTML = ReconciliationDataTitle[3];
      ChildGroupContent3[i].innerHTML = Reconciliationdata.DataComponet[i]["Code"];
      ChildGroupTitle4[i].innerHTML = ReconciliationDataTitle[4];
      ChildGroupContent4[i].innerHTML = Reconciliationdata.DataComponet[i]["BillNumber"];
      ChildGroupTitle5[i].innerHTML = ReconciliationDataTitle[5];
      ReactDOM.render(<ReconciliationBtns />, ChildGroupContent5[i]);
      ReactDOM.render(<ClickMe onClick={() => { UnfoldTd(i) }} />, clickMeSpan[i]);
    }
  }
}
